(async () => {

	const post = await ORM.getRecord('x_6688129130405298176_post', $widget.getParameter('sys_id'));
  	data.post = {};
  	if (!post) {
      return;
    }

  	data.post.title = post.title;
  	data.post.subtitle = post.subtitle;
  	data.post.post = post.post;
  	data.post.banner_image = `/api/attachment/${post.banner_image.sys_id}/${post.banner_image.filename}`;

})()
